import { Annotation, useAnnotations } from '@nivo/annotations'
import { BarAnnotationsProps } from './types'

export const BarAnnotations = <RawDatum,>({ bars, annotations }: BarAnnotationsProps<RawDatum>) => {
    const boundAnnotations = useAnnotations({
        data: bars,
        annotations,
        getPosition: bar => ({
            x: bar.x + bar.width / 2,
            y: bar.y + bar.height / 2,
        }),
        getDimensions: ({ height, width }) => ({
            width,
            height,
            size: Math.max(width, height),
        }),
    })

    return (
        <>
            {boundAnnotations.map((annotation, i) => (
                <Annotation key={i} {...annotation} />
            ))}
        </>
    )
}
import { BoxLegendSvg } from '@nivo/legends'
import { BarLegendProps, LegendData } from './types'

interface BarLegendsProps {
    width: number
    height: number
    legends: [BarLegendProps, LegendData[]][]
    toggleSerie: (id: string | number) => void
}

export const BarLegends = ({ width, height, legends, toggleSerie }: BarLegendsProps) => (
    <>
        {legends.map(([legend, data], i) => (
            <BoxLegendSvg
                key={i}
                {...legend}
                containerWidth={width}
                containerHeight={height}
                data={legend.data ?? data}
                toggleSerie={
                    legend.toggleSerie && legend.dataFrom === 'keys' ? toggleSerie : undefined
                }
            />
        ))}
    </>
)
